INSERT INTO `pages` (`pageid`, `pagename`, `pagelink`, `parentmenu`, `content`, `priority`) VALUES
('home', 'Home', './', '', '<h2>Avishkar ''11</h2> <br /> <br /> <p align="justify">  Over the years, Motilal Nehru National Institute of Technology has identified and inspired young talent and infused in them the spirit to revolutionize the world. Avishkar, one of the largest technological symposiums of the country has kept up this tradition of innovation and breaking unprecedented frontiers. <br /><br /> This three day festival hosted this year from 23rd to 25th September witnesses the conflux of over 6000 enthusiasts hailing from 550 colleges across the nation. It grants these young minds an unparagoned opportunity to showcase their talent and evince their ideas which may lay the groundwork for the next cutting edge technology. <br /><br /> Living up to the name, Avishkar ''11 represents the apogee of science, engineering and path-breaking technology. From guest lectures and paper presentations on one hand to treasure hunts and gaming contests on the other, it plays host to a gamut of activities which push your capacity to ideate, drive your hunger to innovate and challenge your potential to create like never before. Its not just a fest, its a phenomenon! </p>', 1),
('events', 'Events', './events.php', 'home', '<table cellspacing="10">\r\n<tr height="50px">\r\n<td width="400px" align="center" >\r\n<a onClick="refreshPages(''aeromodelling'');">\r\n<p>Aeromodelling</p></a></td> \r\n<td width="400px" align="center">\r\n<a onClick="refreshPages(''cyberquest'');">\r\n<p>CyberQuest</p></a>\r\n</td></tr> \r\n\r\n\r\n<tr height="50px"><td width="400px" align="center"><a onClick="refreshPages(''edifice'');"><p>Edifice</p></a></td> <td width="400px" align="center"><a onClick="refreshPages(''electromania'');"><p>Electromania</p></a></td></tr> <tr height="50px"><td width="400px" align="center"><a onClick="refreshPages(''genesis'');"><p>Genesis</p></a></td> <td width="400px" align="center"><a onClick="refreshPages(''kreedomania'');"><p>Kreedomania</p></a></td></tr> <tr height="50px"><td width="400px" align="center"><a onClick="refreshPages(''mechrocosm'');"><p>MechroCosm</p></a></td> <td width="400px" align="center"><a onClick="refreshPages(''monopoly'');""><p>Monopoly</p></a></td></tr> <tr height="50px"><td width="400px" align="center"><a onClick="refreshPages(''online_events'');"><p>Online Events</p></a></td> <td width="400px" align="center"><a onClick="refreshPages(''rasayans'');"><p>Rasayans</p></a></td></tr><tr height="50px">\r\n<td width="400px" align="center" >\r\n<a onClick="refreshPages(''robomania'');">\r\n<p>Robomania</p></a></td> \r\n<td width="400px" align="center">\r\n<a onClick="refreshPages(''workshops'');">\r\n<p>Workshops</p></a>\r\n</td></tr> \r\n </table>  ', 1),
('hospitality', 'Hospitality', './hospitality.php', 'home', '<br /><p align="justify">All outside participants of AVISHKAR 2011 will be provided with accommodation strictly on sharing Basis. Female Participants will be accommodated at the Girls'' Hostel. We will provide you with bedding, bed sheet, pillow, blanket, mosquito repellant etc. You are expected to follow the discipline of NIT Allahabad during your stay here.<br><br>All the participants opting for accomodation will be provided with bedding from 22<sup>nd</sup> night to 25<sup>th</sup> afternoon 12 o''clock. If you want to stay longer, you will have to give prior information to the hospitality team.\r\n<br><br>\r\nAccommodation will be on the first come first serve basis. So it is advisable to share your travel plans with us well in advance.<br><br><br></p>\r\n', 1),
('accomodation', 'Accomodation', 'accomodation', 'hospitality', '<br><br>\r\n<p align="justify">All outside participants of AVISHKAR 2011 will be provided with accommodation strictly on sharing Basis. Female Participants will be accommodated at the Girls'' Hostel. We will provide you with bedding, bed sheet, pillow, blanket, mosquito repellant etc. You are expected to follow the discipline of NIT Allahabad during your stay here.\r\n<br><br>\r\nAll the participants opting for accomodation will be provided with bedding from 22<sup>nd</sup> night to 25<sup>th</sup> afternoon 12 o''clock. If you want to stay longer, you will have to give prior information to the hospitality team.\r\n<br><br>\r\nAccommodation will be on the first come first serve basis. So it is advisable to share your travel plans with us well in advance.<br><br><br></p>\r\n', 2),
('checking_out', 'Checking Out', 'checking_out', 'hospitality', '<br><br>\r\n<p align="justify">We will provide you with receipts for all your Monetary Transactions with NIT Allahabad and AVISHKAR 2011 which you can get reimbursed at your institute. For Institute Teams Grand Total Bill will be provided to the Team Leader on request (to be made at least three hours before departure).<br><br><br>\r\nCollect you Participation/Winner Certificates (if any) from the Help Desk and the receipts of payments made.<br><br><br>\r\nPlease inform us well in advance about your departure plans.\r\n<br><br><br></p>\r\n', 5),
('fooding', 'Food and Refreshment', 'fooding', 'hospitality', '<br><br><p>All participants of AVISHKAR 2011 who wish to go for meal at our arrangements will be provided food coupons at our Hospitality Partners'' Stall. The food will be hygienic and give you a touch of North-Indian diet.\r\n<br><br><br></p>\r\n', 4),
('at_mnnit', 'At MNNIT', 'at_mnnit', 'hospitality', '<br><br><p>\r\nMNNIT campus itself has a lot of options for the food fanatics. Bikanervala, one of India''s most prominent families in the business of traditional hospitality has an outlet in the college premises with round the clock service. Food fanatics can enjoy a great taste of vegetarian North Indian, South Indian, Continental, Chinese cuisine and Fast food along with a vast variety of traditional Indian sweets and snacks. Also, Amul has its outlet.<br><br><br>\r\nRecently a cafe has also been opened in the boys'' hostel premises. So the college offers a lot of options for your appetite.  \r\n\r\n<br><br><br></p>\r\n', 3),
('travel', 'Travel', 'travel', 'hospitality', '<center><iframe width="550" height="450" frameborder="0" scrolling="no" marginheight="0" marginwidth="0" src="http://maps.google.com/maps?f=q&amp;source=s_q&amp;hl=en&amp;geocode=&amp;q=MNNIT,+Allahabad,+Uttar+Pradesh,+India&amp;aq=0&amp;sll=25.475978,81.845226&amp;sspn=0.019449,0.038581&amp;vpsrc=6&amp;ie=UTF8&amp;hq=MNNIT,&amp;hnear=Allahabad,+Uttar+Pradesh,+India&amp;ll=25.494611,81.865439&amp;spn=0.013558,0.018239&amp;z=15&amp;output=embed"></iframe><br /><small><a href="http://maps.google.com/maps?f=q&amp;source=embed&amp;hl=en&amp;geocode=&amp;q=MNNIT,+Allahabad,+Uttar+Pradesh,+India&amp;aq=0&amp;sll=25.475978,81.845226&amp;sspn=0.019449,0.038581&amp;vpsrc=6&amp;ie=UTF8&amp;hq=MNNIT,&amp;hnear=Allahabad,+Uttar+Pradesh,+India&amp;ll=25.494611,81.865439&amp;spn=0.013558,0.018239&amp;z=15" style="color:#0000FF;text-align:left">View Larger Map</a></small></center><br /><br /><br><br><b>How do I reach Allahabad?</b><br><br>\r\nAllahabad, the headquarters of North Central Railways, is well connected by rail to all parts of India. <br><br>\r\nTo find trains to Allahabad Junction, visit <b><u><a href="http://www.indianrail.gov.in" target="_blank">http://www.indianrail.gov.in</a></u></b>. Allahabad is also connected via air.<br><br>\r\nOther nearby airports are in Varanasi and Lucknow from where you can reach Allahabad by hourly bus services or by train.<br><br><br>\r\n\r\n<b>I am in Allahabad. How do I reach NIT Allahabad?</b><br><br>\r\nYou are probably in Allahabad Junction or Prayag Railway Station or Civil Lines Bus Stand.<br><br>\r\nYou can take a share basis Auto Rickshaw for "Engineering College, Teliarganj" (MNNIT''s Local Name), which will cost you around Rs 10/-.<br><br>\r\nAlternatively you can book an Auto Rickshaw or a cab to NIT Allahabad.<br><br>\r\nThe charges will be Rs 80/- and Rs 120/- respectively.<br><br>\r\nIn case you feel you are lost, just give a ring to our Hospitality Team and we will be there to help you out in no time.<br><br><br>\r\n<br /><br />', 6),
('faq', 'FAQ', 'faq', 'hospitality', '<br><br>\r\n<table>\r\n<tr>\r\n  <td width="50px" ><b>Q .</b></td><td><b>I am ready to leave for Allahabad. What are the must haves?</b></td></tr>\r\n<tr height="8"></tr>\r\n<tr><td width="50px"><b>Ans:</b><br><br><br><br><br></td><td>Allahabad is bit cooler compared to summers and good for vacations. Its moderately hot with temperature variation from 15 to 39&deg;C. Bring your summer clothes with you. We will take care of the rest. And ensure you have the following:<br>\r\n<ul><li>2 Passport Size color photographs<br></li>\r\n<li>Institute Identity Card</li></ul></td></tr>\r\n<tr height="15"></tr>\r\n<tr>\r\n  <td width="50px" ><b>Q .</b></td><td><b>What should I do after reaching NIT Allahabad?</b></td></tr>\r\n<tr height="8"></tr>\r\n<tr><td width="50px" ><b>Ans:</b><br><br><br><br></td><td>Please follow the signs to our Registration Counter and get registered there. You will require 2 passport size color photographs and your Institute Identity Card only. After you are done, rush off to be a part of AVISHKAR 2011 and enjoy your time at MNNIT.</td></tr>\r\n<tr height="15"></tr>\r\n<tr>\r\n  <td width="50px"><b>Q .</b></td><td><b>How do I register?</b></td></tr>\r\n<tr height="8"></tr>\r\n<tr><td width="50px"><b>Ans:</b><br><br></td><td>Log on to <b><u><a href="http://www.avishkar.in/register.php">http://www.avishkar.in/register.php</a></u></b> and register online. That''s all!!! You are now ready to participate in any event of AVISHKAR 2011.</td></tr>\r\n<tr height="15"></tr>\r\n<tr>\r\n  <td width="50px"><b>Q .</b></td><td><b>Who are eligible to Register?</b></td></tr>\r\n<tr height="8"></tr>\r\n<tr><td width="50px"><b>Ans:</b><br><br><br></td><td>AVISHKAR 2011 does not limit creativity and excitement. We welcome students from any stream and any college to AVISHKAR 2011. You just need to have is a valid identity card of your institute</td></tr>\r\n<tr height="15"></tr>\r\n<tr>\r\n  <td width="50px"><b>Q .</b></td><td><b>I have registered online. What now?</b></td></tr>\r\n<tr height="8"></tr>\r\n<tr><td width="50px"><b>Ans:</b><br><br><br><br></td><td>You can start participating in the ongoing Online Events. Just let us know about your travel plans about 5 days in advance so that we can make appropriate arrangements for you. Details like time of Arrival/Mode of Transport will help us in catering to your needs in a better way. You are advised to reserve your tickets in advance to ensure confirmation.</td></tr>\r\n</table><br/><br/>\r\n', 7),
