('mechrosoft', 4, 'Problem Statements', '	<br><br>\r\n   <ul><li><a href="./events/mechrosoft1.pdf" target="_blank">Problem Statement for First Year and Second Year</a><br><br></li>\r\n   <li><a href="./events/mechrosoft2.pdf" target="_blank">Problem Statement for Third Year</a><br><br></li></ul>\r\n   ', 'y'),
('code_masters', 5, 'Contacts', '<br><br>\r\n<table><tr height="40px">\r\n  <td width="400px">Dhanendra Jain</td>\r\n<td width="400px">+91-9457638409</td>\r\n<td width="400px">djains007@gmail.com </td>\r\n</tr>\r\n<tr height="40px">\r\n  <td width="400px">Varuni Garg</td>\r\n<td width="400px"></td>\r\n<td width="400px">meetvaruni@gmail.com </td>\r\n</tr>\r\n</table>', 'y'),
('sambang', 4, 'Problems', '<br><br>\r\n<a href="http://avishkar.in/events/sambang.pdf"><b>Problem_statement_round 1.pdf</b></a><br>\r\n<a href="http://avishkar.in/events/sambang_sample.pdf"><b>Sample_summary__round 1.pdf</b></a><br><br>', 'y'),
('illuminati', 5, 'Contacts', '<br><br>\r\n<table><tr height="40px">\r\n  <td width="400px">Jasleen Singh</td>\r\n<td width="400px">+91-9307016782</td>\r\n<td width="400px">saddy.rocks.89@gmail.com </td>\r\n</tr>\r\n<tr height="40px">\r\n  <td width="400px">Soumi Bandhopadhyay</td>\r\n<td width="400px"></td>\r\n<td width="400px"></td>\r\n</tr>\r\n</table>', 'y'),
